import React, {Component} from 'react'
import Alert from './Alert'
import PropTypes from 'prop-types'

export default class Registro extends Component{
    constructor(props){
        super(props)
        this.state ={
            guardar: this.props.guardar,
            valid: false
        }
    }
    static propTypes = {
        guardar: PropTypes.func.isRequired
    }
    //TODO crear refs
    EventRef = React.createRef();
    TypeRef = React.createRef();
    StartRef = React.createRef();
    DurationRef = React.createRef();
    FinalRef = React.createRef();

    CreateAppoinment = (event) => {
        event.preventDefault()
        const newAppoinment = {
            event: this.DocumentRef.current.value,
            type: this.namesRef.current.value,
            start: this.lastNameRef.current.value,
            duration: this.dateRef.current.value,
            final: this.HourRef.current.value
        }
        newAppoinment.start && newAppoinment.event ? this.state.guardar(newAppoinment) : this.setState({valid: true})
    }
    render(){
        const {valid} = this.state
        return(
            <div className="col-md-7 order-md-1">
            <h4 className="d-flex justify-content-between align-items-center mb-3">
                <span className="text-muted">Nuevo Eento</span>
            </h4>
            {valid && <Alert messege="You should fill all fields"/>}
            <form onSubmit={this.CreateAppoinment}>
                <div>
                    <label>Evento</label>
                    <input ref={this.EventRef} type="text" className="form-control" required />
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <label>Tipo</label>
                        <input ref={this.type} type="text" className="form-control" required />
                    </div>
                    <div className="col-md-6">
                        <label>Inicio</label>
                        <input ref={this.StartRef} type="text" className="form-control" required />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <label>Duraci√≥n</label>
                        <input ref={this.DurationRef} type="number" className="form-control" />
                    </div>                    
                </div>                
                <hr className="mb-4" />
                <button className="btn btn-primary btn-lg btn-block bg-purple text-white lh-100" type="submit">
                    Save
                </button>
            </form>
        </div>
        )    
    }
}
